storiesOf('Components|Atoms/Radio', module)
  .addDecorator(withKnobs)
  .addParameters({
    readme: {
      sidebar: Readme,
    },
    changelog: Changelog,
  })
  .add('Radio', () => {
    const wrapper = document.createElement('div');

    const label = text('label*', '18-45');
    const checked = boolean('checked*', false);
    const focus = boolean('focus*', false);
    const disabled = boolean('disabled', false);
    const button = boolean('button', false);
    const icon = boolean('Icon', false);
    const noGap = boolean('noGap', false);
    const noAutoWidth = boolean('noAutoWidth', false);

    const template = html`
      <axa-text variant="size-3"
        >Knobs with a * only affect the first radio button</axa-text
      >
      <br />
      <axa-fieldset>
        <axa-radio
          name="contract"
          label="${label}"
          ?focus="${focus}"
          ?checked="${checked}"
          icon="${icon ? iconSVG : ''}"
          ?noGap="${noGap}"
          ?button="${button}"
          ?disabled="${disabled}"
          ?noAutoWidth="${noAutoWidth}"
          value="1"
        ></axa-radio>
        <axa-radio
          name="contract"
          icon="${icon ? iconSVG : ''}"
          ?noGap="${noGap}"
          ?button="${button}"
          ?disabled="${disabled}"
          ?noAutoWidth="${noAutoWidth}"
          label="46-54"
          value="2"
        ></axa-radio>
        <axa-radio
          name="contract"
          icon="${icon ? iconSVG : ''}"
          ?noGap="${noGap}"
          ?button="${button}"
          ?disabled="${disabled}"
          ?noAutoWidth="${noAutoWidth}"
          label="55+"
          value="3"
        ></axa-radio>
        <axa-fieldset horizontal></axa-fieldset
      ></axa-fieldset>
    `;

    render(template, wrapper);
    return wrapper;
  });
